CREATE TABLE IF NOT EXISTS `producto` (
  `idproducto` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `idLoginMember` int(10) unsigned NOT NULL DEFAULT '0',
  `codigo` varchar(100) CHARACTER SET utf8 COLLATE utf8_spanish_ci NOT NULL,
  `nombre` varchar(100) CHARACTER SET utf8 COLLATE utf8_spanish_ci NOT NULL,
  `descripcion` varchar(1000) CHARACTER SET utf8 COLLATE utf8_spanish2_ci NOT NULL,
  `brochure` varchar(300) CHARACTER SET utf8 COLLATE utf8_spanish2_ci NOT NULL,
  `foto` varchar(100) CHARACTER SET utf8 COLLATE utf8_spanish_ci NOT NULL,
  `tipo` enum('producto','servicio') NOT NULL DEFAULT 'producto',
  `clasificacion` enum('cloud_ip','planet_voice','planet_ucc','planet_video','inactivo','equipo') NOT NULL DEFAULT 'planet_voice',
  `costoOrigen` float NOT NULL DEFAULT '0',
  `costoFOBMXUS` float NOT NULL DEFAULT '0',
  `CostoMXN` float NOT NULL DEFAULT '0',
  `margenPesos` float NOT NULL DEFAULT '0',
  `margenPorcentaje` int(10) unsigned NOT NULL,
  `precioVenta` float NOT NULL DEFAULT '0',
  `Inventariable` enum('si','no') NOT NULL DEFAULT 'si',
  `comisionMaxima` int(10) unsigned NOT NULL,
  `descuentoMaximo` float NOT NULL DEFAULT '0',
  `estatus` enum('disponible','agotado','baja') NOT NULL DEFAULT 'disponible',
  `unidadesDisponibles` int(10) unsigned NOT NULL,
  `numeroSerie` varchar(30) CHARACTER SET utf8 COLLATE utf8_spanish_ci NOT NULL,
  `idUbicacion` int(10) unsigned NOT NULL DEFAULT '0',
  `fechaBaja` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  PRIMARY KEY (`idproducto`)
) ENGINE=InnoDB  DEFAULT CHARSET=latin1 AUTO_INCREMENT=224 ;



CREATE TABLE IF NOT EXISTS `servicios` (
  `idServicio` int(11) NOT NULL AUTO_INCREMENT,
  `nombre` varchar(50) NOT NULL,
  `descripcion` text NOT NULL,
  `codigo` varchar(25) NOT NULL,
  PRIMARY KEY (`idServicio`)
) ENGINE=InnoDB  DEFAULT CHARSET=latin1 AUTO_INCREMENT=6 ;



CREATE TABLE IF NOT EXISTS `servicio_cloud_ip` (
  `idServicioCloudIp` int(11) NOT NULL AUTO_INCREMENT,
  `id_usuario` int(11) NOT NULL,
  `codigo` varchar(20) NOT NULL,
  `fecha_solicitud` datetime NOT NULL,
  `descripcion` text NOT NULL,
  `proveedor` enum('ETP','MTC','ILOX','LNDR','TLMX') NOT NULL,
  `tipo` enum('Semi-dedicado','Dedicado','ADSL') NOT NULL,
  `capacidad_subida` int(11) NOT NULL,
  `capacidad_bajada` int(11) NOT NULL,
  `nombre_sitio` varchar(100) NOT NULL,
  `direccion` text NOT NULL,
  `gasto_instalacion` double NOT NULL,
  `descuento_instalacion` int(11) NOT NULL DEFAULT '0',
  `renta_mensual` int(11) NOT NULL,
  `descuento_renta` double NOT NULL,
  `plazo` enum('6','12','18','24','36','48') NOT NULL DEFAULT '6',
  `terminos_condiciones` int(11) NOT NULL,
  `observaciones` int(11) NOT NULL,
  PRIMARY KEY (`idServicioCloudIp`)
) ENGINE=InnoDB DEFAULT CHARSET=latin1 AUTO_INCREMENT=1 ;

